package compatterns;

public class numbers1 {

	public static void main(String[] args) {
		java.util.Scanner sc=new java.util.Scanner(System.in);
	    int n=sc.nextInt();
	    for(int i=1;i<=4;i++)
	    {
	        for(int j=1;j<=i;j++)
	        {
	                System.out.print(n+" ");
	                n++;
	       
	        }
	        System.out.println();
	    }
		System.out.println("Hello World");
		// TODO Auto-generated method stub

	}

}
